package org.pikater.web.visualisation.definition.result;

import org.pikater.shared.util.Tuple;
import org.pikater.web.ImageType;
import org.pikater.web.vaadin.gui.server.components.popups.dialogs.ProgressDialog.IProgressDialogResultHandler;
import org.pikater.web.visualisation.DatasetVisualizationValidation;
import org.pikater.web.visualisation.definition.AttrMapping;

/**
 * <p>This class represents the results generated by dataset comparison.
 * In other words, a series of scatter plots that each compare certain column
 * of the first dataset to a certain column of the second dataset.</p>
 * 
 * <p> The columns being compared are determined by
 * {@link DatasetVisualizationValidation#areCompatible(AttrMapping, AttrMapping)}.
 * 
 * @author SkyCrawl
 */
public class DSVisTwoResult extends AbstractDSVisResult<AttrMapping, DSVisTwoSubresult>
{
	public DSVisTwoResult(IProgressDialogResultHandler taskResult, int imageWidth, int imageHeight)
	{
		super(taskResult, imageWidth, imageHeight);
	}
	
	public DSVisTwoSubresult createAndRegisterSubresult(Tuple<AttrMapping, AttrMapping> attrInfo, ImageType imageType)
	{
		DSVisTwoSubresult newImageResult = new DSVisTwoSubresult(imageType, attrInfo);
		registerSubresult(newImageResult.toLeftIndex(), newImageResult.toTopIndex(), newImageResult);
		return newImageResult;
	}
}